    def test_apostrophe(self):
        s = self.parse(name=u"FlexGet's show", data=u"FlexGet's show s01e01")
        assert s.valid
        s = self.parse(name=u"FlexGet's show", data=u"FlexGets show s01e01")
        assert s.valid
