                (nsq.touch,
                    {'id': 'test'},
                    'TOUCH test\n'),
                (nsq.ready,
                    {'count': 100},
                    'RDY 100\n'),
                (nsq.nop,
                    {},
                    'NOP\n'),
                (nsq.pub,
                    {'topic': 'test', 'data': msgs[0]},
                    'PUB test\n' + struct.pack('>l', len(msgs[0])) + msgs[0]),
                (nsq.mpub,
                    {'topic': 'test', 'data': msgs},
                    'MPUB test\n' + struct.pack('>l', len(mpub_body)) + mpub_body)
