#=======================================================================

import cython
cython.declare(BOL=object, EOL=object, EOF=object, NOT_FOUND=object)

import Errors
from Regexps import BOL, EOL, EOF

NOT_FOUND = object()

class Scanner(object):
  """
  A Scanner is used to read tokens from a stream of characters
  using the token set specified by a Plex.Lexicon.

  Constructor:

    Scanner(lexicon, stream, name = '')

      See the docstring of the __init__ method for details.

  Methods:

    See the docstrings of the individual methods for more
    information.

    read() --> (value, text)
      Reads the next lexical token from the stream.

    position() --> (name, line, col)
      Returns the position of the last token read using the
      read() method.

    begin(state_name)
      Causes scanner to change state.

    produce(value [, text])
      Causes return of a token value to the caller of the
      Scanner.

  """

#  lexicon = None        # Lexicon
#  stream = None         # file-like object
#  name = ''
#  buffer = ''
#  buf_start_pos = 0     # position in input of start of buffer
#  next_pos = 0          # position in input of next char to read
#  cur_pos = 0           # position in input of current char
#  cur_line = 1          # line number of current char
#  cur_line_start = 0    # position in input of start of current line
#  start_pos = 0         # position in input of start of token
#  start_line = 0        # line number of start of token
#  start_col = 0         # position in line of start of token
#  text = None           # text of last token read
#  initial_state = None  # Node
#  state_name = ''       # Name of initial state
#  queue = None          # list of tokens to be returned
#  trace = 0

  def __init__(self, lexicon, stream, name = '', initial_pos = None):
    """
    Scanner(lexicon, stream, name = '')

      |lexicon| is a Plex.Lexicon instance specifying the lexical tokens
      to be recognised.

      |stream| can be a file object or anything which implements a
      compatible read() method.

      |name| is optional, and may be the name of the file being
      scanned or any other identifying string.
    """
    self.trace = 0

    self.buffer = u''
    self.buf_start_pos = 0
    self.next_pos = 0
    self.cur_pos = 0
    self.cur_line = 1
    self.start_pos = 0
    self.start_line = 0
    self.start_col = 0
    self.text = None
    self.state_name = None

    self.lexicon = lexicon
    self.stream = stream
    self.name = name
    self.queue = []
    self.initial_state = None
    self.begin('')
    self.next_pos = 0
    self.cur_pos = 0
    self.cur_line_start = 0
    self.cur_char = BOL
    self.input_state = 1
    if initial_pos is not None:
        self.cur_line, self.cur_line_start = initial_pos[1], -initial_pos[2]

  def read(self):
    """
    Read the next lexical token from the stream and return a
    tuple (value, text), where |value| is the value associated with
    the token as specified by the Lexicon, and |text| is the actual
    string read from the stream. Returns (None, '') on end of file.
    """
    queue = self.queue
    while not queue:
      self.text, action = self.scan_a_token()
      if action is None:
        self.produce(None)
        self.eof()
      else:
        value = action.perform(self, self.text)
        if value is not None:
          self.produce(value)
    result = queue[0]
    del queue[0]
    return result

  def scan_a_token(self):
    """
    Read the next input sequence recognised by the machine
    and return (text, action). Returns ('', None) on end of
    file.
    """
    self.start_pos = self.cur_pos
    self.start_line = self.cur_line
    self.start_col = self.cur_pos - self.cur_line_start
    action = self.run_machine_inlined()
    if action is not None:
      if self.trace:
        print("Scanner: read: Performing %s %d:%d" % (
          action, self.start_pos, self.cur_pos))
      text = self.buffer[self.start_pos - self.buf_start_pos :
                         self.cur_pos   - self.buf_start_pos]
      return (text, action)
    else:
      if self.cur_pos == self.start_pos:
        if self.cur_char is EOL:
          self.next_char()
        if self.cur_char is None or self.cur_char is EOF:
          return (u'', None)
      raise Errors.UnrecognizedInput(self, self.state_name)

  def run_machine_inlined(self):
    """
    Inlined version of run_machine for speed.
    """
    state = self.initial_state
    cur_pos = self.cur_pos
    cur_line = self.cur_line
    cur_line_start = self.cur_line_start
    cur_char = self.cur_char
    input_state = self.input_state
    next_pos = self.next_pos
    buffer = self.buffer
    buf_start_pos = self.buf_start_pos
    buf_len = len(buffer)
