class Registry(list):
    def add(self, item):
        for i, r in enumerate(self):
            if r.weight > item.weight:
                self.insert(i, item)
                return

        self.append(item)

    def find_by_name(self, name):
        for i in self:
            if i.name and (i.name == name):
                return i

    def remove_by_name(self, name):
        for i, r in enumerate(self):
            if r.name and (r.name == name):
                return self.pop(i)


