def upload_to(name):
    def f(project, filename):
        extension = filename.split('.')[-1]
        timestamp = timezone.now().strftime('%Y%m%d%H%M%S')
        return '%s_%s_%s.%s' % (name, project.id, timestamp, extension)
    return f


