        self.failUnless(cash2Lock.alive)
        self.failUnless(cash2Lock.started)
        self.assertEquals(cash2Lock.acquireCounts.keys(), [ 'cash_2' ])
        self.assertEquals(cash2Lock.acquireCounts['cash_2'], 1)
