frame1 = tk.Frame(
    master = window,
    bg = '#808000')
frame1.pack(fill='both', expand='yes')
editArea = tkst.ScrolledText(
    master = frame1,
    wrap   = tk.WORD,
    width  = 80,
    height = 40)
