    def remove(self, text):
        """
        remove a menu entry
        
        :param text: string with entry description
        """
        #print(self.items)
        #print("wanna remove:", text, func)
        for (t, f) in self.items:
            if t == text:
                break
        else:
            print("did not found text")
            return
        self.items.remove((t, f))
    
