while len(g.roomnames[where].connections) > 0:
    output("You are now here:\n{}".format(g.roomnames[where].info(g)))
    where = nextMove(g, where)
    output("\n"*15)
output("\n"*5)
output("Thank you for playing. Have a nice real life")







