def create_index(table, columns):
    name = "%s_%s" % (table, "_".join(columns))
    cursor.execute("DROP INDEX IF EXISTS %s" % name)
    cursor.execute("CREATE INDEX %s ON %s (%s)" % (name, table, ", ".join(columns)))

