# Scenario: Add and then delete each role.
def test_role(role):
    try:
        instance.execute(
            ["sudo", "criticctl", "addrole",
             "--name", "alice",
             "--role", role])
    except testing.virtualbox.GuestCommandError as error:
        logger.error("correct criticctl usage failed:\n%s"
                     % error.stdout)
    else:
        try:
            instance.execute(
                ["sudo", "criticctl", "delrole",
                 "--name", "alice",
                 "--role", role])
        except testing.virtualbox.GuestCommandError as error:
            logger.error("correct criticctl usage failed:\n%s"
                         % error.stdout)
for role in ROLES:
    test_role(role)

