@register.filter
def signup_status(ts, event):
    if ts["signedUp"]:
        return 'signed-up'
    elif not places_left(ts):
        if event["allowWaitList"]:
            if ts["onWaitList"]:
                return 'on-waiting-list'
            else:
                return 'waiting-list'
        else:
            return 'full'
    else:
        return 'available'

