        self.assertEqual(['a', 'b', 'c', 'd', 'e'], disc_attr.values)
        self.assertEqual(ranges, disc_attr.ranges)
        self.assertEqual(1, disc_attr.index)
        self.assertEqual(attr.DISCRETE, disc_attr.type)
        
