        p = self.parent()
        c = self.firstChild()
        M = []  # list of children
        while c:
            M.append(c)
            c = c.nextSibling()
        last = self
        for c in M:
            self.takeItem(c)
            p.insertItem(c)
            c.moveItem(last)
