        L = [(n1,l) for n1,n2,l in self.edges if n2==n]
        while L and L[0][0].getNumber() >= num:
            n = L[0][0]
            L = [(n1,l) for n1,n2,l in self.edges if n2==n]
        else:
            if L:
                return L[0][0]
        return None
            
