PATTERNS_ = [('PV', 'C', 'L'),
             ('PV', 'H', 'S'),
             ('PV', 'C'),
             ('PV', 'L'),
             ('PV', 'S'),
             ('PV', 'H'),]


def ThaiFix(string):
  try: ustring = unicode(string, 'utf8')
  except UnicodeDecodeError: ustring = string
  i = 0
  length = len(ustring)
  nustring = []
  while i < length:
    c1 = c2 = c3 = ''
    try: 
      u1 = ustring[i]
      c1 = ThaiTable[u1][0]
    except (KeyError, IndexError): pass
    try:
      u2 = ustring[i+1]
      c2 = ThaiTable[u2][0]
    except (KeyError, IndexError): pass
    try:
      u3 = ustring[i+2]
      c3 = ThaiTable[u3][0]
    except (KeyError, IndexError): pass
    if (c1, c2, c3) in PATTERNS_:
      nustring += [u2, u3, u1]
      i += 3
    elif (c1, c2) in PATTERNS_:
      nustring += [u2, u1]
      i += 2
    else:
      nustring += [u1]
      i += 1
  return ''.join(nustring).encode('utf8')

