c_use_array      = numba.addressof(use_array)
c_get_attributes = numba.addressof(get_attributes)

c_use_array.argtypes      = [ctypes.POINTER(CtypesArray)]
c_get_attributes.argtypes = [ctypes.POINTER(CtypesArray)]

