Array = namedtuple('Array', ['handle', 'array', 'data', 'shape', 'strides'])

def make_array():
    """Make a double[*, 10] ctypes-allocated array"""
    empty = lambda c_type, args: ctypes.cast(
        (c_type * len(args))(*args), ctypes.POINTER(c_type))

