        if pp_id:
            pps = PrivateProposal.objects.filter(
                    proposal_id=pp_id).values_list(
                            'id', flat=True)
            if pps:
                qs = qs.filter(proposals__in=pps)
            else:
                qs = qs.none()
        if knesset_booklet:
            kps = KnessetProposal.objects.filter(
                    booklet_number=knesset_booklet).values_list(
                            'id', flat=True)
