    for tag in tags:
        try:
            t = tag.find('a',{'style':"color:Black;"}).contents[0]
        except UnicodeEncodeError:
            logger.warn('error parsing roles tag: %s' % tag.find('a',{'style':"color:Black;"}).contents[0])
