class Out(object):
    def __init__(self):
        self.is_message = False
        self.is_print = False
        self.text = ''


class Message(Out):
    def __init__(self, symbol, tag, text):
        super(Message, self).__init__()
        self.is_message = True
        self.symbol = symbol
        self.tag = tag
        self.text = text

    def __str__(self):
        return ' : ' + self.text

    def __cmp__(self, other):
        if self.is_message == other.is_message and self.text == other.text:
            return 0
        else:
            return 1

    def get_data(self):
        return {
            'message': True,
            'symbol': self.symbol,
            'tag': self.tag,
            'prefix': u'%s::%s' % (self.symbol, self.tag),
            'text': self.text,
        }


class Print(Out):
    def __init__(self, text):
        super(Print, self).__init__()
        self.is_print = True
        self.text = text

    def __str__(self):
        return ' | ' + self.text

    def __cmp__(self, other):
        if self.is_message == other.is_message and self.text == other.text:
            return 0
        else:
            return 1

    def get_data(self):
        return {
            'message': False,
            'text': self.text,
        }


