    def up(self):
        retcode = self.run('svn', 'revert', '--recursive', '.')[0]
        if retcode != 0:
            raise ProjectImportError(
                ("Failed to get code from '%s' (svn revert): %s"
                 % (self.repo_url, retcode))
            )
        retcode = self.run('svn', 'up', '--accept', 'theirs-full',
                           '--trust-server-cert', '--non-interactive')[0]
