         ["small",  ["small",  "Small",    "sm",  "sm."]],
         ["medium", ["medium", "Medium",   "med", "med.", "Med", "Med."]],
         ["large",  ["large",  "Large",    "lg",  "lg.",  "Lg",  "Lg."]],
         ["box",    ["box",    "Box",      "bx"]],
         ["whole",  ["whole",  "whl",      "wh."]],
