WM_LBUTTONUP     = 514
WM_LBUTTONDBLCLK = 515
WM_RBUTTONDOWN   = 516
WM_RBUTTONUP     = 517
WM_RBUTTONDBLCLK = 518
WM_MBUTTONDOWN   = 519
WM_MBUTTONUP     = 520
WM_MBUTTONDBLCLK = 521
