        obj = super(W_Object, self).__deepcopy__(memo)
        obj.map = copy.deepcopy(self.map, memo)
        obj.object_storage = copy.deepcopy(self.object_storage, memo)
        obj.unboxed_storage = copy.deepcopy(self.unboxed_storage, memo)
