def view_project_widgets(request, project_slug):
    project = get_object_or_404(Project, slug=project_slug)
    if project.private:
        raise PermissionDenied
    resources = Resource.objects.filter(project=project)
    if len(resources) > 0:
        default_resource = resources[0]
