        query = Q()
        fields = self.model._meta.get_all_field_names()
        for arg in args:
            if hasattr(self.model, arg) and args[arg]:
                kwargs = {str(arg + '__id'): long(args[arg])}
                query = query & Q(**kwargs)
            elif arg in fields:
                kwargs = {arg: args[arg]}
                query = query & Q(**kwargs)
