class Line(Shape):
    """ line segment from point1 to point2 """

    def __init__(self, p1, p2):
        super(Line, self).__init__()
        self.type = "line"
        self.p1 = Point(p1)
        self.p2 = Point(p2)


    def __eq__(self, other):
        return (super(Line, self).__eq__(other) and
                (self.type == other.type and
                 self.p1 == other.p1 and
                 self.p2 == other.p2))


    def min_point(self):
        """ Return the min point of the shape """
        x = self.p1.x
        if self.p2.x < x:
            x = self.p2.x
        y = self.p1.y
        if self.p2.y < y:
            y = self.p2.y
        return Point(x, y)


    def max_point(self):
        """ Return the max point of the shape """
        x = self.p1.x
        if self.p2.x > x:
            x = self.p2.x
        y = self.p1.y
        if self.p2.y > y:
            y = self.p2.y
        return Point(x, y)


    def move(self, x, y):
        """ Move the line to an explicit position. """
        dx = x - self.p1.x
        dy = y - self.p1.y

        self.p1.x += dx
        self.p2.x += dx
        self.p1.y += dy
        self.p2.y += dy


