class Bundle:
    """ bundle_descriptor """
    function = 'bundle'

    def __init__(self):
        self.bundle_id = None
        self.nets = None
        self.gap = []

    class Gap:
        """ A Gap """
        function = 'gap'

        def __init__(self):
            self.gap = None
            self.layer = []

        def parse(self, args):
            """ parse a gap """
            assert len(args) >= 1
            self.gap = int(args[0])
            for arg in args[1:]:
                assert arg[0] == 'layer'
                # FIXME
                self.layer = arg[1:]

    def parse(self, args):
        """ parse a bundle """
        assert len(args) >= 2
        assert args[1][0] == 'nets'

        self.bundle_id = args[0]
        self.nets = args[1][1:]
        for arg in args[2:]:
            assert arg[0] == 'gap'
            self.gap.append(Bundle.Gap(arg[1:]))

