class Circle(ShapeBase):
    """ circle_descriptor """
    function = 'circle'

    def __init__(self):
        self.layer_id = 'signal'
        self.diameter = None
        self.vertex = (0, 0)

    def parse(self, args):
        """ parse a circle """
        assert len(args) in (2, 4)
        self.layer_id = args[0]
        self.diameter = float(args[1])
        if len(args) == 4:
            self.vertex = (float(args[2]), float(args[3]))
        else:
            self.vertex = (0, 0)

    def compose(self):
        """ componse a circle """
        return [
            self.function,
            self.layer_id,
            self.diameter,
            self.vertex[0], self.vertex[1]
        ]

class QArc(ShapeBase):
    """ quarc_descriptor """
    function = 'qarc'

    def __init__(self):
        self.layer_id = 'signal'
        self.aperture_width = 0
        self.vertex1 = (0, 0)
        self.vertex2 = (0, 0)
        self.vertex3 = (0, 0)

    def parse(self, args):
        """ parse a QArc """
        assert len(args) == 5
        self.layer_id = pop_string(args)
        self.aperture_width = pop_string(args)
        self.vertex1 = pop_vertex(args)
        self.vertex2 = pop_vertex(args)
        self.vertex3 = pop_vertex(args)
        assert_empty(self, args)

    def compose(self):
        """ compose a QArc """
        return [
            self.function,
            self.layer_id,
            self.aperture_width,
            self.vertex1[0], self.vertex1[1],
            self.vertex2[0], self.vertex2[1],
            self.vertex3[0], self.vertex3[1],
        ]

class Polygon(ShapeBase):
    """ polygon_descriptor """
    function = 'polygon'

    def __init__(self):
        self.layer_id = 'signal'
        self.aperture_width = 0
        self.vertex = []

    def parse(self, args):
        """ parse a polygon """
        assert len(args) > 3
        self.layer_id = pop_string(args)
        self.aperture_width = pop_type(args, basestring)
        self.vertex = pop_vertexes(args)
        assert_empty(self, args) 

    def compose(self):
        """ compose a polygon """
        result = [
            self.function,
            self.layer_id,
            self.aperture_width,
            ]
        for v in self.vertex:
            result.extend(v)
        return result

